import React, {Component} from 'react';

class Form1 extends Component {
    state = {userName:''}
    changeHandler = (event) =>{
        this.setState({userName:event.target.value})
    }

    render() {
        return (
            <div>
                <form action="">
                    <h3>User Name</h3>
                    <input type="text" onChange={this.changeHandler}/>
                    <h1>User:{this.state.userName}</h1>
                </form>
            </div>
        );
    }
}

export default Form1;
