import React, {Component} from 'react';
import {ListGroup, ListGroupItem} from 'reactstrap';
class Category extends Component {
    state = {
            categories : [
                { name: "Beverage", id: 1 },
                { name: "Food", id: 2},
                { name: "Products", id: 3 },
                { name: "Other", id: 4},
            ],
    }
    getCategories = () =>{
        fetch("")
    }
    render() {
        return (
            <div className="category">
                <h2>{this.props.info.title}</h2>

                <ListGroup className="">
                    {
                        this.state.categories.map(item =>(
                            <ListGroupItem className="items2" key={item.id} onClick={()=>this.props.changeCategorory(item.name)}>
                               {item.name}
                            </ListGroupItem>
                        ))
                    }
                </ListGroup>
            </div>  
        )
    }
}

export default Category;
