import React, {Component} from 'react';
import { Table, Button} from "reactstrap";

class Product extends Component {
    state = {
        cart: [],
        totalPrice: 0
    }

    addToCart = (productData)=>{
        var cartAll = this.state.cart;
        var total = this.state.totalPrice
        total = total + Number(productData.unitPrice);
        cartAll.push(productData)
        this.setState({cart: cartAll});
        this.setState({totalPrice: total});
        console.log(total)
    }

    render() {
        return (
            <div className="product">
                <ul>
                    {
                        this.state.cart.map(item => (
                            <li key={item.id}>{item.unitPrice}$ - {item.productName}</li>
                        ))
                    }
                    <hr/>
                    <b>TOTAL PRICE : {this.state.totalPrice}$</b>
                </ul>
                <h3>{this.props.currentCategory} {this.props.info.title}</h3>
                <Table>
                    <thead>
                    <tr>
                        <th>#</th>
                        <th>Product Name</th>
                        <th>Quantity Per Unit</th>
                        <th>Price</th>
                        <th>Units In Stock</th>
                        <th>Cart</th>
                    </tr>
                    </thead>
                    <tbody>
                        {
                            this.props.data.map(item => (
                                <tr key={item.id}>
                                    <th scope="row">{item.id}</th>
                                    <td>{item.productName}</td>
                                    <td>{item.quantityPerUnit}</td>
                                    <td>{item.unitPrice}$</td>
                                    <td>{item.unitsInStock}</td>
                                    <td>
                                        <Button
                                            outline color={item.unitsInStock === 0 ? "secondary":"dark"}
                                            title="Add to cart"
                                            onClick = {()=> this.addToCart(item)}
                                            disabled={item.unitsInStock === 0}>
                                            <i className="fas fa-shopping-cart"/>
                                        </Button>
                                    </td>
                                </tr>
                            ))
                        }
                    </tbody>
                </Table>
            </div>
        );
    }
}

export default Product;
