import React, {Component} from 'react';
import {ListGroup, ListGroupItem} from 'reactstrap';
class Category extends Component {
    constructor(props) {
        super(props);
        this.state = {
            categories: [
                {
                    categoryID:1,
                    categoryName: "Beverage"
                },
                {
                    categoryID:2,
                    categoryName: "Futires"
                },
                {
                    categoryID:3,
                    categoryName: "Foods"
                },
                {
                    categoryID:4,
                    categoryName: "Drinks"
                },

            ]
        }
    }
    render() {
        return (
            <div className="category">
                <h2>{this.props.info.title}</h2>

                <ListGroup className="category bg-dark">
                    {
                        this.state.categories.map(item =>(
                            <ListGroupItem className="items2"><a href={item.categoryName.toLocaleLowerCase()} className="links2">{item.categoryName}</a></ListGroupItem>
                        ))
                    }
                </ListGroup>
            </div>
        );
    }
}

export default Category;
